// Program.cs or Startup.cs

var builder = WebApplication.CreateBuilder(args);

builder.WebHost.UseSentry(o => {
    o.Dsn = "https://examplePublicKey@o0.ingest.sentry.io/0";

    // Environment from ASPNETCORE_ENVIRONMENT
    o.Environment = builder.Environment.EnvironmentName;

    // Release version
    o.Release = "my-aspnet-app@1.0.0";

    // Performance monitoring
    o.TracesSampleRate = 0.1;

    // Capture user context from HttpContext.User
    o.SendDefaultPii = false;

    // Breadcrumb settings
    o.MaxBreadcrumbs = 50;

    // Filter out health checks from transactions
    o.SetBeforeSendTransaction((transaction) => {
        if (transaction.Name == "GET /health")
            return null;
        return transaction;
    });
});
