using Sentry;

SentrySdk.Init(o => {
    // Your DSN from Sentry project settings
    o.Dsn = "https://examplePublicKey@o0.ingest.sentry.io/0";

    // Identify the environment
    o.Environment = "production";

    // Track which version is deployed
    o.Release = "my-dotnet-app@1.0.0";
});
