configCode:
SentrySdk.Init(o => {
    o.Dsn = "https://examplePublicKey@o0.ingest.sentry.io/0";
    o.Environment = "production";
    o.Release = "my-dotnet-app@1.0.0";

    // Performance monitoring
    o.TracesSampleRate = 0.1;

    // Custom traces sampler
    o.TracesSampler = context => {
        // Don't trace health checks
        if (context.TransactionContext.Name == "GET /health")
            return 0.0;

        // Sample API calls at 10%
        return 0.1;
    };

    // Profiling (10% of traced transactions)
    o.ProfilesSampleRate = 0.1;

    // Filter events before sending
    o.SetBeforeSend((sentryEvent, hint) => {
        // Filter out specific exceptions
        if (sentryEvent.Exception is IgnoredException)
            return null;

        // Scrub sensitive data
        if (sentryEvent.Extra.ContainsKey("password"))
            sentryEvent.Extra.Remove("password");

        return sentryEvent;
    });

    // Include only app code in stack traces
    o.AddInAppExclude("Microsoft.");
    o.AddInAppExclude("System.");
    o.AddInAppInclude("MyApp.");

    // Debug mode
    o.Debug = false;

    // Attach stacktrace to all events
    o.AttachStacktrace = true;
});
