import io.sentry.Sentry;
import io.sentry.SentryOptions;
import io.sentry.SamplingContext;

Sentry.init(options -> {
    options.setDsn("https://examplePublicKey@o0.ingest.sentry.io/0");
    options.setEnvironment("production");
    options.setRelease("my-java-app@1.0.0");

    // Performance monitoring
    options.setTracesSampleRate(0.1);

    // Custom traces sampler
    options.setTracesSampler((SamplingContext context) -> {
        String transactionName = context.getTransactionContext().getName();

        // Don't trace health checks
        if (transactionName.equals("GET /health")) {
            return 0.0;
        }

        // Sample API calls at 10%
        return 0.1;
    });

    // Filter events before sending
    options.setBeforeSend((event, hint) -> {
        // Filter out specific exceptions
        if (event.getThrowable() instanceof IgnoredException) {
            return null;
        }
        return event;
    });

    // Include only app code in stack traces
    options.addInAppInclude("com.mycompany.myapp");
    options.addInAppExclude("org.springframework");

    // Capture user context
    options.setSendDefaultPii(false);

    // Debug mode
    options.setDebug(false);

    // Attach stacktrace to all events
    options.setAttachStacktrace(true);
});
