import io.sentry.Sentry;
import io.sentry.spring.boot.SentryAutoConfiguration;

// application.properties or application.yml approach:
// sentry.dsn=https://examplePublicKey@o0.ingest.sentry.io/0
// sentry.environment=production
// sentry.traces-sample-rate=0.1

// Programmatic configuration:
Sentry.init(options -> {
    options.setDsn("https://examplePublicKey@o0.ingest.sentry.io/0");

    // Environment from Spring profile
    options.setEnvironment(System.getProperty("spring.profiles.active", "production"));

    // Release version
    options.setRelease("my-spring-app@1.0.0");

    // Performance monitoring
    options.setTracesSampleRate(0.1);

    // Capture user context
    options.setSendDefaultPii(false);

    // Breadcrumb settings
    options.setMaxBreadcrumbs(50);
});
