configCode:
Sentry.init({
  dsn: "https://examplePublicKey@o0.ingest.sentry.io/0",
  environment: "production",
  release: "my-app@2.0.0",

  // === Error Filtering ===

  // Ignore known browser errors that aren't actionable
  ignoreErrors: [
    'Script error',
    'ResizeObserver loop limit exceeded',
    /NetworkError/i,
  ],

  // Ignore errors from browser extensions (not your code)
  denyUrls: [
    /chrome-extension:/,
    /extensions\//,
    /^file:\/\//,
  ],

  // Only capture errors from your domain
  allowUrls: [
    /https?:\/\/(www\.)?myapp\.com/,
  ],

  // === Sampling for Quota Management ===

  // Capture 50% of errors (reduces quota usage)
  sampleRate: 0.5,

  // === Performance Monitoring ===

  // Capture 10% of transactions for performance data
  tracesSampleRate: 0.1,

  // Or use custom sampler for more control
  tracesSampler: (samplingContext) => {
    // Sample 100% of critical API calls
    if (samplingContext.request?.url?.includes('/api/checkout')) {
      return 1.0;
    }
    // Sample 10% of everything else
    return 0.1;
  },

  // Filter out health check transactions (noise reduction)
  ignoreTransactions: [
    'GET /health',
    'GET /ping',
    /^OPTIONS/,
  ],

  // Profile 10% of sampled transactions (CPU/memory profiling)
  profilesSampleRate: 0.1,
});
