use sentry;
use sentry::types::Dsn;

fn main() {
    let _guard = sentry::init(sentry::ClientOptions {
        dsn: Some("https://examplePublicKey@o0.ingest.sentry.io/0".into()),
        environment: Some("production".into()),
        release: Some("my-rust-service@1.0.0".into()),

        // Enable tracing
        traces_sample_rate: 0.1,

        // Sample 100% of errors
        sample_rate: 1.0,

        // Filter events before sending
        before_send: Some(Box::new(|event| {
            // Filter out specific errors
            if event.message.as_ref().map_or(false, |m| m.contains("harmless")) {
                return None;
            }
            Some(event)
        })),

        // Debug mode for development
        debug: false,

        // Include in-app frames only
        in_app_include: vec!["my_crate"],

        ..Default::default()
    });
}
