beforeBreadcrumbCode:
(breadcrumb, hint) => {
  const message = breadcrumb.message || '';

  // Categorize by service based on message prefix
  if (message.includes('[PaymentService]')) {
    breadcrumb.category = 'payment';
    breadcrumb.level = 'info';
  } else if (message.includes('[AuthService]')) {
    breadcrumb.category = 'auth';
    breadcrumb.level = 'info';
  } else if (message.includes('[UserService]')) {
    breadcrumb.category = 'user';
  }

  // Upgrade level for error keywords
  if (message.toLowerCase().includes('error') || message.toLowerCase().includes('failed')) {
    breadcrumb.level = 'error';
  } else if (message.toLowerCase().includes('warning') || message.toLowerCase().includes('warn')) {
    breadcrumb.level = 'warning';
  }

  // Clean up message by removing service prefix
  breadcrumb.message = message.replace(/\[[^\]]+\]\s*/, '');

  return breadcrumb;
}
