beforeSendCode:
// Drop events from test environment
if let Some(tags) = event.get("tags") {
    if let Some(env) = tags.get("environment") {
        if env == "test" {
            // Don't send test events to production Sentry
            return None;
        }
    }
}

// Drop debug and info level events
if let Some(level) = event.get("level").and_then(|l| l.as_str()) {
    if level == "debug" || level == "info" {
        // Only send warnings and errors
        return None;
    }
}

// Add tag indicating filtering was applied
event["tags"]["filtered"] = json!("true");

Some(event)
