beforeSendTransactionCode:
(transaction, hint) => {
  // Drop health check and monitoring transactions
  const dropPatterns = ['/health', '/ready', '/metrics', '/ping', '/livez', '/readyz'];
  const txName = transaction.transaction || '';

  if (dropPatterns.some(p => txName.includes(p))) {
    return null; // Drop the transaction
  }

  return transaction;
}
