beforeBreadcrumbCode:
(breadcrumb, hint) => {
  // Drop HTTP breadcrumbs for noisy third-party services
  if (breadcrumb.category === 'fetch' || breadcrumb.category === 'xhr') {
    const url = breadcrumb.data?.url || '';

    const noisyPatterns = [
      // Analytics
      'google-analytics.com',
      'analytics.google.com',
      'segment.io',
      'mixpanel.com',
      'amplitude.com',
      'hotjar.com',

      // Ads
      'doubleclick.net',
      'googlesyndication.com',
      'googleadservices.com',
      'facebook.com/tr',

      // Monitoring (you're already using Sentry!)
      'newrelic.com',
      'datadog',

      // Internal health checks
      '/health',
      '/ping',
      '/metrics'
    ];

    if (noisyPatterns.some(pattern => url.includes(pattern))) {
      return null;
    }
  }

  return breadcrumb;
}
