beforeSendCode:
// Redact user email and IP
if (ev.User != null)
{
    if (!string.IsNullOrEmpty(ev.User.Email))
    {
        ev.User.Email = "[REDACTED]";
    }
    if (!string.IsNullOrEmpty(ev.User.IpAddress))
    {
        ev.User.IpAddress = null;
    }
}

// Add tag indicating PII was scrubbed
ev.SetTag("pii_scrubbed", "true");
ev.SetTag("data_privacy", "compliant");

// Note: In production, you would also redact message and extra data
// using regex patterns for emails, phones, SSNs similar to:
// System.Text.RegularExpressions.Regex.Replace(text, pattern, "[REDACTED]")

return ev;
